/**
 * App components
 *
 * ApproveToken.js:
 * Renders the wallet screen for the Approve screen.
 * It loads when the user clicks the approve button.
 * For ERC20 tokens, it asks for the address to approve and the balance to approve for that address.
 * For ERC721, it asks for the address to approve and the token ID to approve.
 *
 * Author: Truong Thinh
 * Create At: 5/ 8/ 2021
 */
import React from "react";
import InputField from "./InputField";

function ApproveToken(props) {
  return (
    <div className="panel-block is-paddingless is-12">
      <div className="column is-12" id="token-lists">
        <InputField
          onInputChangeUpdateField={props.onInputChangeUpdateField}
          fields={props.fields}
          name="receiver"
          placeholder="Address"
        />

        {props.approveDetail20 ? (
          <div>
            <InputField
              onInputChangeUpdateField={props.onInputChangeUpdateField}
              fields={props.fields}
              name="amount"
              placeholder="Amount To Approve"
              addon={props.approveDetail20.symbol.toUpperCase()}
            />

            <InputField
              onInputChangeUpdateField={props.onInputChangeUpdateField}
              default={props.defaultGasPrice}
              fields={props.fields}
              name="gasPrice"
              placeholder="Gas Price In Gwei"
              addon="Gas Price(gwei)"
            />

            <InputField
              onInputChangeUpdateField={props.onInputChangeUpdateField}
              default={props.defaultGasLimit}
              fields={props.fields}
              name="gasLimit"
              placeholder="Gas Limit"
              addon="Gas Limit"
            />

            <div className="field is-grouped is-pulled-right is-offset">
              <p className="control">
                <a
                  className="button is-light"
                  disabled={props.inProgress}
                  onClick={() => props.closeTransfer()}
                >
                  Back
                </a>
              </p>
              <p className="control">
                <a
                  className={
                    props.inProgress
                      ? "button is-danger is-loading"
                      : "button is-danger"
                  }
                  disabled={props.inProgress}
                  onClick={() => props.Approve()}
                >
                  Approve
                </a>
              </p>
            </div>
          </div>
        ) : (
          <div>
            <InputField
              onInputChangeUpdateField={props.onInputChangeUpdateField}
              fields={props.fields}
              name="tokenid"
              placeholder="Token ID"
            />

            <InputField
              onInputChangeUpdateField={props.onInputChangeUpdateField}
              default={props.defaultGasPrice}
              fields={props.fields}
              name="gasPrice"
              placeholder="Gas Price In Gwei"
              addon="Gas Price(gwei)"
            />

            <InputField
              onInputChangeUpdateField={props.onInputChangeUpdateField}
              default={props.defaultGasLimit}
              fields={props.fields}
              name="gasLimit"
              placeholder="Gas Limit"
              addon="Gas Limit"
            />

            <div className="field is-grouped is-pulled-right is-offset">
              <p className="control">
                <a
                  className="button is-light"
                  disabled={props.inProgress}
                  onClick={() => props.closeTransfer()}
                >
                  Back
                </a>
              </p>
              <p className="control">
                <a
                  className={
                    props.inProgress
                      ? "button is-danger is-loading"
                      : "button is-danger"
                  }
                  disabled={props.inProgress}
                  onClick={() => props.Approve()}
                >
                  Approve
                </a>
              </p>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default ApproveToken;
